import React, { Component } from 'react'

class Form extends Component {
	constructor(props) {
		super(props)

		this.state = {
			Discription: '',
			Amount: '',
			TransactionType: 'react'
		}
	}

	handleDiscriptionChange = event => {
		this.setState({
			Discription: event.target.value
		})
	}

	handleAmountChange = event => {
		this.setState({
			Amount: event.target.value
		})
	}

	handleTransactionTypeChange = event => {
		this.setState({
			TransactionType: event.target.value
		})
	}

	handleSubmit = event => {
		alert(`${this.state.Discription} ${this.state.Amount} ${this.state.TransactionType}`)
		event.preventDefault()
	}

	render() {
		const { Discription, Amount, TransactionType } = this.state
		return (
			<form onSubmit={this.handleSubmit}>
				<div>
					<label>Username </label>
					<input
						type="text"
						value={Discription}
						onChange={this.handleDiscriptionChange}
					/>
				</div>
				<div>
					<label>Amount</label>
					<textarea
						value={Amount}
						onChange={this.handleAmountChange}
					/>
				</div>
				<div>
					<label>TransactionType</label>
					<select value={TransactionType} onChange={this.handleTransactionTypeChange}>
						<option value="Debit">React</option>
						<option value="Credit">Angular</option>
						<option value="vue">Vue</option>
					</select>
				</div>
				<button type="submit">Submit</button>
			</form>
		)
	}
}

export default Form
