class Form extends Component {
	constructor(props) {
		super(props)

		this.state = {
			Amount: '',
			Discription: '',
			TransactionType: 'Debit',
      Date : '17-03-2021'
		}
	}
  handleAmountChange = event => {
		this.setState({
			Amount: event.target.value
		})
	}
	handleAmountChange = event => {
		this.setState({
			Amount: event.target.value
		})
	}

	handleDiscriptionChange = event => {
		this.setState({
			Discription: event.target.value
		})
	}

	handleTransactionTypeChange = event => {
		this.setState({
			TransactionType: event.target.value
		})
	}

	handleSubmit = event => {
		alert(`${this.state.Amount} ${this.state.Discription} ${this.state.TransactionType}`)
		event.preventDefault()
	}

	render() {
		const { Amount, Discription, TransactionType } = this.state
		return (
			<form onSubmit={this.handleSubmit}>
        <div>
					<label>Date </label>
					<input
						type="date"
						value={Date}
						onChange={this.handleDateChange}
					/>
				</div>
				<div>
					<label>Amount </label>
					<input
						type="text"
						value={Amount}
						onChange={this.handleAmountChange}
					/>
				</div>
				<div>
					<label>Discription</label>
					<textarea
						value={Discription}
						onChange={this.handleDiscriptionChange}
					/>
				</div>
				<div>
					<label>TransactionType</label>
					<select value={TransactionType} onChange={this.handleTransactionTypeChange}>
						<option value="Debit">Debit</option>
						<option value="Credit">Credit</option>
					</select>
				</div>
				<button type="submit">Submit</button>
			</form>
		)
	}
}

export default Form
