import React, { Component } from 'react'

class Form extends Component {

    constructor(props) {
        super(props)
    
        this.state = {
             TransactionType : 'Debit',
             Amount : '',
             Description : ''

        }
    }
    
    render() {
        return (
            <form>
            <div>
                   <label>Transaction Type</label>
                   <select value={this.state.TransactionType}>
                       <option>Credit</option>
                       <option>Debit</option>
                   </select>
               </div>
               <div>
                   <label>Amount</label>
                   <input type="number" value={this.state.Amount}></input>
               </div>
               <div>
                   <label>Description</label>
                   <textarea value={this.state.Description}></textarea>
               </div>
            </form>
        )
    }
}

export default Form
