import axios from 'axios'
import React, { Component } from 'react'

class Table extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
             data : [],
        }
    }

    componentDidMount(){
        axios.get("http://localhost:8090/get")
        .then(response =>{
            console.log(response)
            this.setState({data :response})
        })
        .catch(error =>{
            console.log(error)
        })
    }
    
    tableStyle = {
        color: "black",
        padding: "10px",
        fontFamily: "Arial",
        border : 'solid'
    }

    render() {
        const {data} = this.state
        return (
            <div>
                <table style = {this.tableStyle}>
                <tr style = {{    border : 'solid'}}>
                        <td>Date</td>
                        <td>Description</td>
                        <td>Debit</td>
                        <td>Credit</td>
                        <td>Balance</td>
                    </tr>
                    {
                    data.length ?
                    data.map(data => <tr style = {this.tableStyle}>
                        <td style = {this.tableStyle}>{data.Amount}</td>
                        
                    </tr>) :
                    null
                     }
                </table>
            </div>
        )
    }
}

export default Table
