    // }

    // buyLaptop=()=>{
    //     this.setState({
    //         numOfLaptops : this.state.numOfLaptops - 1
    //     })
    // }

    render() {
        return (
            <div>
                <h1 className="title">Welcome to Vshop</h1>
                <div className="item">
                    <p>Dell Inspiron Laptop</p>
                    <p>Available:{this.props.numOfLaptops}</p>
                    <button onClick={this.buyLaptop}>BUY</button>
                </div>
            </div>
        )
    }
}

const mapStateToProps = (state) =>{
    return {
        numOfLaptops : state.numOfLaptops
    }
}

const mapDispatchToProps = (dispatch) =>{
    return{
        buyLaptop
    }
}
export default connect(mapStateToProps,null)(Shop)
